package com.fernandez.api.articles.service.impl;

import com.fernandez.api.articles.common.Messages;
import com.fernandez.api.articles.constants.PropertiesConstant;
import com.fernandez.api.articles.dto.ArticleDTO;
import com.fernandez.api.articles.dto.ComentariosDTO;
import com.fernandez.api.articles.dto.TagDTO;
import com.fernandez.api.articles.dto.UserDTO;
import com.fernandez.api.articles.exceptions.ArticlesLogicException;
import com.fernandez.api.articles.model.Comentarios;
import com.fernandez.api.articles.model.ComentariosUserNotRegistered;
import com.fernandez.api.articles.model.User;
import com.fernandez.api.articles.repository.CommentsRepository;
import com.fernandez.api.articles.repository.UserRepository;
import com.fernandez.api.articles.service.ComentarioService;
import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.modelmapper.ModelMapper;
import org.springframework.data.domain.Page;
import org.springframework.data.domain.Pageable;
import org.springframework.http.HttpStatus;
import org.springframework.stereotype.Service;

import javax.transaction.Transactional;
import java.util.List;
import java.util.Objects;
import java.util.Optional;

@Slf4j
@Service
@Transactional
@RequiredArgsConstructor
public class ComentarioServiceImpl implements ComentarioService {

    private final UserRepository userRepository;

    private final CommentsRepository commentsRepository;

    private final ModelMapper modelMapper = new ModelMapper();

    private final Messages messages;

    @Override
    public List<ComentariosDTO> findAllComentariosByBlogTranslationId(final long comentarioId, final long level, Long blogsTranslation, final List<ComentariosDTO> comentariosList) {
        List<Comentarios> comentarios = commentsRepository.findAllByParentIdAndArticleId(comentarioId, blogsTranslation);
        if (comentarios != null) {
            for (Comentarios comentario : comentarios) {
                comentariosList.add(mapFromDtoToEntity(comentario, level));
                findAllComentariosByBlogTranslationId(comentario.getId(), level + 1, blogsTranslation, comentariosList);
            }
        }
        return comentariosList;
    }

    @Override
    public ComentariosDTO save(final ComentariosDTO comentariosDTO) throws Exception {
        String username , email;
        User foundByEmail = null, foundByUsername = null;
        ComentariosUserNotRegistered comentariosUserNotRegistered = new ComentariosUserNotRegistered();
        Comentarios comentarios = modelMapper.map(comentariosDTO, Comentarios.class);
        if (Objects.nonNull(comentariosDTO.getUsername()) && Objects.nonNull(comentariosDTO.getUsername())) {

            foundByEmail = userRepository.findByEmail(comentariosDTO.getEmail());
            foundByUsername = userRepository.findByUsername(comentariosDTO.getUsername());

        }
        
        if(Objects.isNull(foundByEmail) && Objects.isNull(foundByUsername)) {
            comentariosUserNotRegistered.setEmail(comentariosDTO.getEmail());
            comentariosUserNotRegistered.setUsername(comentariosDTO.getUsername());
            comentarios.setComentarioUserNotRegistered(comentariosUserNotRegistered);
        }else{
            comentarios.setAuthorComment(foundByEmail);
        }
        comentarios.setLevel(comentariosDTO.getLevel());
        return modelMapper.map(commentsRepository.save(comentarios), ComentariosDTO.class);
    }

    @Override
    public ComentariosDTO findCommentById(Long commentId) {
        Comentarios foundComentario = commentsRepository.getOne(commentId);
        ComentariosDTO comentariosDTO = modelMapper.map(foundComentario,ComentariosDTO.class);
        if(Objects.nonNull(foundComentario.getComentarioUserNotRegistered ())){
            UserDTO userDTO = new UserDTO();
            userDTO.setUsername(foundComentario.getComentarioUserNotRegistered().getUsername());
            userDTO.setEmail(foundComentario.getComentarioUserNotRegistered().getEmail());
            userDTO.setImageProfile("data:image/jpeg;base64,");
            comentariosDTO.setAuthorComment(userDTO);
        }
        return comentariosDTO;
    }

    @Override
    public void deleteById(Long commentId) {
        commentsRepository.delete(commentsRepository.findById(commentId)
                .orElseThrow(() -> new ArticlesLogicException(HttpStatus.NOT_FOUND, messages.get(PropertiesConstant.COMMENT_NOT_FOUND))));
    }

    @Override
    public Page <ComentariosDTO> findAllComments ( final String acceptLanguage , final Pageable pageable ) {
        return commentsRepository.findAll(pageable).map(this::mapToDto);
    }

    private ComentariosDTO mapToDto ( final Comentarios comentarios ) {
        return modelMapper.map(comentarios,ComentariosDTO.class);
    }

    private ComentariosDTO mapFromDtoToEntity(final Comentarios comentarios,final Long level) {
        ComentariosDTO comentariosDTO = modelMapper.map(comentarios,ComentariosDTO.class);
        if(Objects.nonNull(comentarios.getComentarioUserNotRegistered())){
            UserDTO userDTO = new UserDTO();
            userDTO.setUsername(comentarios.getComentarioUserNotRegistered().getUsername());
            userDTO.setEmail(comentarios.getComentarioUserNotRegistered().getEmail());
            userDTO.setImageProfile("data:image/jpeg;base64,");
            comentariosDTO.setAuthorComment(userDTO);
        }
        comentarios.setParentId(comentarios.getParentId());
        comentariosDTO.setLevel(level);
        return comentariosDTO;
    }
}